import "./App.css";
import { useState } from "react";

import pitch from "./pitch.png";
import lines from "./lines.png";
import circles from "./circles.png";
import squares from "./squares.png";

const Field = (param) => {
  const [imageParam, setImageParam] = useState();
  const getImagePattern = () => {
    switch (param) {
      case "Lines":
        return lines;
      case "Squares":
        return squares;
      case "Circles":
        return circles;
      default:
        return circles;
    }
  };

  const images = [`url(${pitch})`];
  images.push(`url(${getImagePattern()})`);
  images.join(",");

  console.log(images);
  return (
    <div
      style={{
        backgroundColor: "#588f58",
        backgroundImage: images,
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        width: "100%",
        height: "700px",
      }}
    ></div>
  );
};

export default Field;
