import { useState, useEffect } from "react";
import "./App.css";
import Field from "./field.jsx";
import circles from "./circles.png";
import squares from "./squares.png";
import lines from "./lines.png";

function App() {
  const options = [
    {
      label: "Circles",
      value: circles,
    },
    {
      label: "Lines",
      value: lines,
    },
    {
      label: "Squares",
      value: squares,
    },
  ];
  const [pattern, setPattern] = useState(circles);

  return (
    <div className="App">
      <Field param={pattern}></Field>
      <select onChange={(e) => setPattern(e.target.value)}>
        {options.map((option) => (
          <option value={option.value}>{option.label}</option>
        ))}
      </select>
    </div>
  );
}

export default App;
