import { useState } from "react";
import "./squadSize.css";

const SquadSize = ({ selectSquadNumber }) => {
  const [squadSize, setSquadSize] = useState(8);
  selectSquadNumber.selectSquadNumber(squadSize);

  return (
    <div>
      <div className="content">
        <p className="title">Squad Size</p>
        <div className="btns">
          <div className="btn" onClick={selectSquadNumber(5)}>
            5
          </div>
          <div className="btn" onClick={selectSquadNumber(7)}>
            7
          </div>
          <div className="btn" onClick={selectSquadNumber(8)}>
            8
          </div>
          <div className="btn" onClick={selectSquadNumber(9)}>
            9
          </div>
          <div className="btn" onClick={selectSquadNumber(11)}>
            11
          </div>
        </div>
      </div>
    </div>
  );
};

export default SquadSize;
