import "./squadColors.css";
import { SketchPicker, SketchPickerProps } from "react-color";
import { useState } from "react";

const SquadColors = ({ setSquadColors }) => {
  const [toggleColorPickerBase, setToggleColorPickerBase] = useState(false);
  const [toggleColorPickerSecond, setToggleColorPickerSecond] = useState(false);
  const [toggleColorPickerThird, setToggleColorPickerThird] = useState(false);
  const [baseColorsPicked, setBaseColorsPicked] = useState("black");
  const [secondColorsPicked, setsecondColorsPicked] = useState("black");
  const [thirdColorsPicked, setthirdColorsPicked] = useState("black");

  const toggleBaseColor = () => {
    setToggleColorPickerBase(!toggleColorPickerBase);
  };
  const toggleThird = () => {
    setToggleColorPickerSecond(!toggleColorPickerSecond);
  };
  const toggleSecond = () => {
    setToggleColorPickerThird(!toggleColorPickerThird);
  };
  const handleBaseColor = (e) => {
    setBaseColorsPicked(e.hex);
    console.log(e.hex);
  };

  return (
    <div>
      <div className="content">
        <p className="title">Select team design</p>
        <div className="wrapper-btn">
          <div
            className="btn-color"
            style={{ backgroundColor: baseColorsPicked }}
            onClick={toggleBaseColor}
          >
            {toggleColorPickerBase && (
              <div>
                <SketchPicker
                  color={baseColorsPicked}
                  onChange={handleBaseColor}
                  style={{ userSelect: "none" }}
                ></SketchPicker>
              </div>
            )}
          </div>
          <p className="subtitle">Base Color</p>
        </div>
        <div className="wrapper-btn">
          <div
            className="btn-color"
            style={{ backgroundColor: secondColorsPicked }}
            onClick={toggleSecond}
          >
            {toggleColorPickerSecond && (
              <div>
                <SketchPicker
                  color={secondColorsPicked}
                  onChange={handleChangeComplete}
                  style={{ userSelect: "none" }}
                ></SketchPicker>
              </div>
            )}
          </div>
          <p className="subtitle">Base Color</p>
        </div>
        <div className="wrapper-btn">
          <div
            className="btn-color"
            style={{ backgroundColor: thirdColorsPicked }}
            onClick={toggleThird}
          >
            {toggleColorPickerThird && (
              <div>
                <SketchPicker
                  color={thirdColorsPicked}
                  onChange={handleChangeComplete}
                  style={{ userSelect: "none" }}
                ></SketchPicker>
              </div>
            )}
          </div>
          <p className="subtitle">Base Color</p>
        </div>
      </div>
    </div>
  );
};

export default SquadColors;
