import "./squadColors.css";
import { SketchPicker } from "react-color";
import { useState } from "react";

const SquadColors = ({ setSquadColors }) => {
  const [toggleColorPicker, setToggleColorPicker] = useState(false);
  const [colorsPicked, setColorsPicked] = useState();

  const toggleNav = () => {
    setToggleColorPicker(!toggleColorPicker);
  };
  const handleChangeComplete = (e) => {
    setColorsPicked(e);
  };

  return (
    <div>
      <div className="content">
        <p className="title">Select team design</p>
        <div
          className="btn-color"
          style={{ backgroundColor: "black" }}
          onClick={setToggleColorPicker}
        >
          {toggleColorPicker && (
            <div>
              <SketchPicker
                color={colorsPicked}
                onChangeComplete={handleChangeComplete}
              >
                Base color
              </SketchPicker>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SquadColors;
