import "./squadColors.css";
import { SketchPicker, SketchPickerProps } from "react-color";
import { useState } from "react";

const SquadColors = ({ setSquadColors }) => {
  const [toggleColorPicker, setToggleColorPicker] = useState(false);
  const [colorsPicked, setColorsPicked] = useState("black");

  const toggleBaseColor = () => {
    setToggleColorPicker(!toggleColorPicker);
  };
  const toggleThird = () => {
    setToggleColorPicker(!toggleColorPicker);
  };
  const toggleSecond = () => {
    setToggleColorPicker(!toggleColorPicker);
  };
  const handleChangeComplete = (e) => {
    setColorsPicked(e.hex);
    console.log(e.hex);
  };

  return (
    <div>
      <div className="content">
        <p className="title">Select team design</p>
        <div className="wrapper-btn">
          <div
            className="btn-color"
            style={{ backgroundColor: colorsPicked }}
            onClick={toggleNav}
          >
            {toggleColorPicker && (
              <div>
                <SketchPicker
                  color={colorsPicked}
                  onChange={handleChangeComplete}
                  style={{ userSelect: "none" }}
                ></SketchPicker>
              </div>
            )}
          </div>
          <p className="subtitle">Base Color</p>
        </div>
        <div className="wrapper-btn">
          <div
            className="btn-color"
            style={{ backgroundColor: colorsPicked }}
            onClick={toggleNav}
          >
            {toggleColorPicker && (
              <div>
                <SketchPicker
                  color={colorsPicked}
                  onChange={handleChangeComplete}
                  style={{ userSelect: "none" }}
                ></SketchPicker>
              </div>
            )}
          </div>
          <p className="subtitle">Base Color</p>
        </div>
        <div className="wrapper-btn">
          <div
            className="btn-color"
            style={{ backgroundColor: colorsPicked }}
            onClick={toggleNav}
          >
            {toggleColorPicker && (
              <div>
                <SketchPicker
                  color={colorsPicked}
                  onChange={handleChangeComplete}
                  style={{ userSelect: "none" }}
                ></SketchPicker>
              </div>
            )}
          </div>
          <p className="subtitle">Base Color</p>
        </div>
      </div>
    </div>
  );
};

export default SquadColors;
